  function makeApiCall(requestBody, card) {
    fetch(API_SET_CARD_PUBLISH, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(requestBody)
    })
      .then(response => {
        if (!response.ok) {
          console.error('âŒ API response not ok:');
          throw new Error(`Failed to update card: ${response.status} ${response.statusText}`);
        }
        return response.json();
      })
      .then(data => {
        console.log('âœ… API call successful:', data);
        if (requestBody.completed) {
          console.log('ðŸŽ‰ Card completed successfully!');
        } else if (requestBody.published !== undefined) {
          renderCardList(currentTypeFilter, currentCategoryFilter);
          const toggle = document.querySelector(`#toggle-${requestBody.one_thing_user_card_id}`);
          if (toggle) {
            toggle.disabled = false;
            toggle.checked = requestBody.published; // Set toggle to match published state
            console.log('âœ… Updated toggle state for card:', requestBody.one_thing_user_card_id);
          } else {
            console.log('âš ï¸ Toggle not found for card:', requestBody.one_thing_user_card_id);
          }
          showSuccessTooltip();
          console.log('ðŸŽ‰ Successfully updated card publish state!');
        }
      })
      .catch(error => {
        console.error('âŒ Error updating card:', error);
        console.error('âŒ Error details:', {
          message: error.message,
          stack: error.stack,
          name: error.name
        });
        if (requestBody.published !== undefined) {
          const toggle = document.querySelector(`#toggle-${requestBody.one_thing_user_card_id}`);
          if (toggle) {
            toggle.disabled = false;
            toggle.checked = !requestBody.published; // Reset to previous state
            console.log('ðŸ”„ Reset toggle to previous state due to error');
          }
        }
      });
  }
